// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IOFT } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oft/interfaces/IOFT.sol";
import { OrderBaseUpgradeable } from "./base/OrderBaseUpgradeable.sol";
import { OrderSafeStorage } from "./storage/OrderSafeStorage.sol";
import { IOrderSafe, MessagingReceipt, OFTReceipt } from "./interfaces/IOrderSafe.sol";
import { IOrderSafeRelayer, StakeMsg } from "./interfaces/IOrderSafeRelayer.sol";

contract OrderSafe is IOrderSafe, OrderBaseUpgradeable, OrderSafeStorage {
    // using OFTComposeMsgCodec for bytes;
    using SafeERC20 for IERC20;

    function stakeOrder(
        address _staker,
        uint256 _amount
    ) public payable whenNotPaused returns (MessagingReceipt memory msgReceipt, OFTReceipt memory oftReceipt) {
        require(msg.sender == _staker, "OrderSafe: sender must be staker");
        // Get the token address from the of contract, it could be the oft contract itself or a native erc20 contract
        IERC20 token = IERC20(IOFT(oft).token());

        // Transfer the amount from the staker to the relayer contract
        // Staker should approve the safe contract to spend the amount first
        token.safeTransferFrom(_staker, safeRelayer, _amount);
        // Call the relayer contract to send the stake message
        (msgReceipt, oftReceipt) = IOrderSafeRelayer(safeRelayer).sendStakeMsg{ value: msg.value }(_staker, _amount);
        emit OrderStaked(_staker, _amount);
    }

    function relayStakeOrder(
        address _staker,
        uint256 _amount
    ) public payable whenNotPaused returns (MessagingReceipt memory msgReceipt, OFTReceipt memory oftReceipt) {
        require(msg.sender == _staker, "OrderSafe: sender must be staker");
        // Get the token address from the of contract, it could be the oft contract itself or a native erc20 contract
        IERC20 token = IERC20(IOFT(oft).token());

        // Transfer the amount from the staker to the relayer contract
        // Staker should approve the safe contract to spend the amount first
        token.safeTransferFrom(_staker, safeRelayer, _amount);
        // Call the relayer contract to send the stake message
        (msgReceipt, oftReceipt) = IOrderSafeRelayer(safeRelayer).relayStakeMsg{ value: msg.value }(_staker, _amount);
        emit OrderStaked(_staker, _amount);
    }

    function unstakeOrder(address _staker, uint256 _amount) public whenNotPaused {
        require(msg.sender == _staker, "OrderSafe: sender must be staker");
        // Call the relayer contract to send the unstake message
        IOrderSafeRelayer(safeRelayer).sendUnstakeMsg(_staker, _amount);
    }

    /* ========================= Only Owner ========================= */

    function setOrderRelayer(address _orderRelayer) public onlyOwner {
        require(_orderRelayer != address(0), "OrderSafe: invalid order relayer address");
        safeRelayer = _orderRelayer;
        emit OrderRelayerSet(_orderRelayer);
    }

    function setOft(address _oft) public onlyOwner {
        require(_oft != address(0), "OrderSafe: invalid oft address");
        oft = _oft;
    }

    /* ========================= View ========================= */

    function getStakeFee(address _staker, uint256 _amount) public view returns (uint256) {
        return IOrderSafeRelayer(safeRelayer).getStakeFee(_staker, _amount);
    }

    function getRelayStakeFee(address _staker, uint256 _amount) public view returns (uint256) {
        return IOrderSafeRelayer(safeRelayer).getRelayStakeFee(_staker, _amount);
    }
}

contracts/crosschain/OrderSafeRelayer.so.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { OFTMsgCodec } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oft/OFTCore.sol";
import { IOFT, SendParam, MessagingFee, MessagingReceipt, OFTReceipt } from "../layerzerolabs/lz-evm-oapp-v2/contracts/oft/interfaces/IOFT.sol";
import { OptionsBuilder } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/libs/OptionsBuilder.sol";
import { OrderRelayerBase } from "./base/OrderRelayerBase.sol";
import { IOrderSafeRelayer } from "./interfaces/IOrderSafeRelayer.sol";
import { Options } from "./interfaces/IOrderRelayer.sol";
import { OrderRelayerStorage } from "./storage/OrderRelayerStorage.sol";
import { OrderSafeRelayerStorage } from "./storage/OrderSafeRelayerStorage.sol";
import { OCCMsgCodec } from "../layerzerolabs/lz-evm-oapp-v2/contracts/oft/libs/OCCMsgCodec.sol";

contract OrderSafeRelayer is IOrderSafeRelayer, OrderRelayerBase, OrderSafeRelayerStorage {
    using SafeERC20 for IERC20;
    using OptionsBuilder for bytes;

    function sendStakeMsg(
        address _staker,
        uint256 _amount
    ) public payable returns (MessagingReceipt memory msgReceipt, OFTReceipt memory oftReceipt) {
        require(msg.sender == orderSafe, "OrderSafeRelayer: Only OrderSafe can call");
        if (IOFT(oft).approvalRequired()) {
            IERC20 token = IERC20(IOFT(oft).token());
            token.approve(oft, _amount);
        }
        uint32 orderEid = _getOrderEid();
        bytes memory options = _getOption(uint8(Options.STAKE_ORDER));
        bytes memory stakeMsg = abi.encode(_staker, _amount);
        SendParam memory sendParam = SendParam({
            dstEid: orderEid,
            to: OFTMsgCodec.addressToBytes32(orderBoxRelayer),
            amountLD: _amount,
            minAmountLD: _amount,
            extraOptions: options,
            composeMsg: stakeMsg,
            oftCmd: ""
        });

        MessagingFee memory fee = IOFT(oft).quoteSend(sendParam, false);
        require(msg.value >= fee.nativeFee, "OrderSafeRelayer: insufficient lz fee");
        (msgReceipt, oftReceipt) = IOFT(oft).send{ value: fee.nativeFee }(sendParam, fee, payable(_staker));
        emit SendStakeMsg(orderEid, OFTMsgCodec.addressToBytes32(orderBoxRelayer), stakeMsg);
    }

    function relayStakeMsg(
        address _staker,
        uint256 _amount
    ) public payable returns (MessagingReceipt memory msgReceipt, OFTReceipt memory oftReceipt) {
        require(msg.sender == orderSafe, "OrderSafeRelayer: Only OrderSafe can call");
        if (IOFT(oft).approvalRequired()) {
            IERC20 token = IERC20(IOFT(oft).token());
            token.approve(oft, _amount);
        }
        uint32 orderEid = _getOrderEid();
        uint128 lzReceiveGas = 200000;
        uint128 airdropValue = 0;
        bytes memory options = OptionsBuilder.newOptions().addExecutorLzReceiveOption(lzReceiveGas, airdropValue);
        bytes memory stakeMsg = abi.encode(_staker, _amount);
        SendParam memory sendParam = SendParam({
            dstEid: orderEid,
            to: OFTMsgCodec.addressToBytes32(orderBoxRelayer),
            amountLD: _amount,
            minAmountLD: _amount,
            extraOptions: options,
            composeMsg: stakeMsg,
            oftCmd: ""
        });

        MessagingFee memory fee = IOFT(oft).quoteRelay(sendParam, false);
        // require(msg.value >= fee.nativeFee, "OrderSafeRelayer: insufficient lz fee");
        (msgReceipt, oftReceipt) = IOFT(oft).relay{ value: fee.nativeFee }(sendParam, fee, payable(_staker));
        emit SendStakeMsg(orderEid, OFTMsgCodec.addressToBytes32(orderBoxRelayer), stakeMsg);
    }

    function sendUnstakeMsg(address _staker, uint256 _amount) public {}

    /* ========================= Only Owner ========================= */

    // Set the order safe address on vault side
    function setOrderSafe(address _orderSafe) public onlyOwner {
        require(_orderSafe != address(0), "OrderSafeRelayer: zero order safe address");
        orderSafe = _orderSafe;
    }

    function setOrderBoxRelayer(address _orderBoxRelayer) public onlyOwner {
        require(_orderBoxRelayer != address(0), "OrderSafeRelayer: zero order box relayer address");
        orderBoxRelayer = _orderBoxRelayer;
    }

    function setOrderChainId(uint256 _orderChainId, uint32 _orderEid) public onlyOwner {
        require(_orderChainId > 0, "OrderSafeRelayer: zero order chain id");
        require(_orderEid > 0, "OrderSafeRelayer: zero order eid");
        orderChainId = _orderChainId;
        eidMap[_orderChainId] = _orderEid;
        chainIdMap[_orderEid] = _orderChainId;
    }

    /* ========================= View ========================= */

    function getStakeFee(address _staker, uint256 _amount) public view override returns (uint256) {
        uint16 index = 0;
        uint128 lzReceiveGas = 200000;
        uint128 lzComposeGas = 500000;
        uint128 airdropValue = 0;
        bytes memory options = OptionsBuilder
            .newOptions()
            .addExecutorLzReceiveOption(lzReceiveGas, airdropValue)
            .addExecutorLzComposeOption(index, lzComposeGas, airdropValue);
        bytes memory composeMsg = abi.encode(_staker, _amount);
        SendParam memory sendParam = SendParam({
            dstEid: _getOrderEid(),
            to: OFTMsgCodec.addressToBytes32(orderBoxRelayer),
            amountLD: _amount,
            minAmountLD: _amount,
            extraOptions: options,
            composeMsg: composeMsg,
            oftCmd: ""
        });

        MessagingFee memory fee = IOFT(oft).quoteSend(sendParam, false);
        return fee.nativeFee;
    }

    function getRelayStakeFee(address _staker, uint256 _amount) public view returns (uint256) {
        uint128 lzReceiveGas = 200000;
        uint128 airdropValue = 0;
        bytes memory options = OptionsBuilder.newOptions().addExecutorLzReceiveOption(lzReceiveGas, airdropValue);
        bytes memory stakeMsg = abi.encode(_staker, _amount);
        SendParam memory sendParam = SendParam({
            dstEid: _getOrderEid(),
            to: OFTMsgCodec.addressToBytes32(orderBoxRelayer),
            amountLD: _amount,
            minAmountLD: _amount,
            extraOptions: options,
            composeMsg: stakeMsg,
            oftCmd: ""
        });

        MessagingFee memory fee = IOFT(oft).quoteRelay(sendParam, false);
        return fee.nativeFee;
    }

    /* ========================= Internal ========================= */

    function _getOrderEid() internal view returns (uint32) {
        return eidMap[orderChainId];
    }
}

